mapper.select:
		SELECT
			const_id, const_code, const_name,  deleted,
			start_date, end_date,
			created_at, updated_at
		FROM pt_construction
        
	        
				AND const_id = #{constId}
	        
            
				AND const_code like CONCAT('%', #{constCode},'%')
	        
	        
	        	AND const_name like CONCAT('%', #{constName},'%')
	        
            
				AND deleted = #{deleted}
	        
    	
        GROUP BY const_id;

		SELECT
			const_id, const_code, const_name,  deleted,
			start_date, end_date,
			created_at, updated_at
		FROM pt_construction
		
			
				AND const_code like CONCAT('%', #{constCode},'%')
	        
	        
	        	AND const_name like CONCAT('%', #{constName},'%')
	        


	        
	            AND pt_construction.start_date  >=  #{targetStartDt}
	        
	        
	            AND pt_construction.start_date  <=  #{targetEndDt}
	        
	        
	            AND pt_construction.end_date  >=  #{targetStartDt}
	        
	        
	            AND pt_construction.end_date  <=  #{targetEndDt}
	        
    	
		ORDER BY pt_construction.end_date;

mapper.update:
update pt_construction
const_name = #{constName}, const_code = #{constCode}, start_date = #{startDate}, end_date = #{endDate}, updated_at = CURRENT_TIMESTAMP
WHERE const_id = #{constId}
