mapper.select:
select COALESCE(MAX(priv_const_id), 0) from pt_privconst
WHERE priv_const_code = #{privconstCode}
LOCK IN SHARE MODE

update.foreach:
update pt_privconst

   updated_at = #{item.updatedAt}

      , priv_const_name = #{item.privConstName}


      , user_id = #{item.userId}


  WHERE priv_const_code = #{item.privConstCode}

		update pt_privconst

            updated_at = #{item.updatedAt},
            deleted = #{item.deleted}

		    WHERE priv_const_code = #{item.privConstCode}

mapper.update:
UPDATE pt_privconst, ( SELECT * from pt_privconst WHERE const_id= #{constId} ) PCON
pt_privconst.const_id = NULL
WHERE pt_privconst.priv_const_id = PCON.priv_const_id;

	UPDATE
		pt_privconst

	    	updated_at = #{ptPrivconstRepository.updatedAt}
	    	, max_report_no = max_report_no + 1

  	WHERE priv_const_code = #{ptPrivconstRepository.privConstCode}

	UPDATE
		pt_privconst

	    	updated_at = #{ptPrivconstRepository.updatedAt}
	    	, max_order_no = max_order_no + 1

  	WHERE priv_const_code = #{ptPrivconstRepository.privConstCode}

	UPDATE
		pt_privconst

	    	updated_at = #{ptPrivconstRepository.updatedAt}
	    	, max_work_no = max_work_no + 1

  	WHERE priv_const_code = #{ptPrivconstRepository.privConstCode}
